static int msm_dai_q6_asrc_start_put(
	struct snd_kcontrol *kcontrol, struct snd_ctl_elem_value *ucontrol)
{
	int ret = -1;
	asrc_enable = ucontrol->value.integer.value[0];

	if(asrc_enable)
		ret = asrc_start();
	else
		pr_err(" %s : ASRC config is not set.\n",__func__);

	return ret;
}

